<textarea class="form-control" @if(!empty($name)) name="{{ $name }}" @endif @if(!$autosize) rows="{{ $rows }}" @endif @if(!empty($placeholder)) placeholder="{{ $placeholder }}" @endif @if(!empty($value)) value="{{ $value }}" @endif @if($disabled) disabled @endif @if($readonly) readonly @endif @if($autosize) data-bs-toggle="autosize" style="overflow: hidden; overflow-wrap: break-word; resize: none; text-align: start; height: 60px;" @endif>{{ $slot }}</textarea>